mapper.select:
SELECT e.ev_cd, e.ev_nm, e.ev_start_ymd, e.ev_end_ymd FROM events e
${searchCate} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY ev_cd DESC LIMIT #{pageable.rowPerPage} OFFSET #{pageable.offset};

SELECT ew_cd, ew_nm, ew_reg_ymd FROM events_winners
${searchCate} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY ew_cd DESC LIMIT #{pageable.rowPerPage} OFFSET #{pageable.offset};

mapper.update:
UPDATE event_participations SET ev_winner_yn = 'Y'
ev_winner_yn = 'N'
AND ev_cd=#{evCd} AND id = #{id}
