mapper.select:
SELECT e.ev_cd, e.ev_nm, e.ev_start_ymd, e.ev_end_ymd FROM events e
e.ev_nm LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY ev_cd DESC LIMIT #{pageable.rowPerPage} OFFSET #{pageable.offset};

SELECT * FROM events_winners ew
ew.ew_nm LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY ew_nm DESC LIMIT #{pageable.rowPerPage} OFFSET #{pageable.offset};
