mapper.update:
/* 특정 게임 정보 수정 */
UPDATE 
	game_detail gd INNER JOIN games g
	ON gd.gm_cd = g.gm_cd
SET
	g.gm_nm=#{gameName},
	gd.`description`=#{description},
	gd.price=#{price}
WHERE 
	gd.gm_cd = #{gameCode};

/* 게임삭제(soft delete) */
UPDATE
	games g INNER JOIN game_detail gd
	ON g.gm_cd = gd.gm_cd
SET
	g.isDelete = 'delete';
	gd.isDelete = 'delete';
where
	g.gm_cd = #{gameCode};
