mapper.select:
SELECT ntc_no, ntc_title, ntc_cn, ntc_img, ntc_view, admin_id, reg_ymd FROM ntc_mttr WHERE 1=1
AND ntc_title LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY ntc_no DESC LIMIT #{rowPerPage} OFFSET #{offset}

mapper.update:
		UPDATE
			 ntc_mttr
		
			
				ntc_title = #{noticeTitle},
			
			
				ntc_cn = #{noticeContent}
			
		
            ,file_idx = #{fileIdx}
        
		
	WHERE
		ntc_no=#{noticeNum};
