mapper.select:
/* 아이템 검색 */ SELECT ps.ps_cd, ps.ps_cate_cd, ps.ps_nm, ps.ps_price, ps.file_idx, pc.cate_nm, ps.is_delete FROM point_shop ps INNER JOIN pointshop_categories pc ON ps.ps_cate_cd = pc.cate_cd
ps.ps_cate_cd=#{searchCate}
AND ps.ps_nm LIKE CONCAT('%', #{searchValue},'%')
ORDER BY CAST(SUBSTRING_INDEX(ps.ps_cd, 'ps_', -1) AS UNSIGNED);

/* 아이템 갯수 총 행의 갯수 */
SELECT
	COUNT(*)
FROM
	point_shop;

mapper.delete:
    	/* 아이템 삭제 */
    	DELETE 
		FROM 
			point_shop 
		WHERE
			ps_cd=#{itemCd};

mapper.update:
    	/*아이템 판매 중지*/
    	UPDATE point_shop
		SET
			is_delete='delete'
		WHERE
			ps_cd=#{itemCd};
